var SearchController = {
  run: function(e) {
    var input = $('#search input'), tasks;

    if (input.val().length == 0 && !$('#search-results').is(':visible')) {
      return;
    }

    if (input.val() === defaultFieldValues.search || input.val().length == 0) {
      $('#search-todo-items').html('<li>' + Feedback.message('error', 'Enter a longer search phrase.') + '</li>');
      return;
    }

    if (!$('#search-results').is(':visible')) {
      $('.outline-view .selected').removeClass('selected');
      $('.content').hide();
      $('#search-results').show();
      $('#project-todo-items').removeClass('todo-items');
      $('#search-todo-items').addClass('todo-items');
    }

    tasks = Task.search(input.val());

    if (tasks.length === 0) {
      $('#search-todo-items').html('<li>' + Feedback.info('error', 'No results found.') + '</li>');
    } else {
      TasksController.display(tasks);
    }
    resize();

    e.preventDefault();
  },

  timer: function() {
    var searchText = $('#search input').val();

    function timer() {
      var newText = $('#search input').val();
      if (searchText != newText) {
        $('#search form input').trigger('change');
        searchText = newText;
      }
      setTimeout(timer, 1000);
    }

    timer();
  },

  installEvents: function() {
    $('#search form').submit(function(e) { e.preventDefault(); });
    $('#search form input').change(SearchController.run);
    $('#search form input').blur(function() {
      var input = $('#search form input');
      if (input.val().trim().length === 0)
        input.val(defaultFieldValues.search);
    });
    $('#search form input').click(function(e) {
      if ($('#search form input').val() === defaultFieldValues.search) {
        $('#search form input').val('');
      }
    });

    SearchController.timer();
  }
};

SearchController.installEvents();

function initKeyboardHandlers() {
  var nextResponder = $();

  function saveField(e) {
    saveEditable();
    closeEditable();
  }

  $('.todo-items :input').live('focus', function() {
    nextResponder = $(this).closest('li').next();
  });

  $(document).bind('keyup', function(e) {
    if (e.which === 27) {
      // [esc]
      if ($('.task.details').length > 0
          && $('.editable .field').length === 0) {
        var task = $('.task.details');
        TasksController.closeEditors();
        task.find('.button').click();
      } else if ($('.editable .field').length > 0) {
        closeEditable();
        nextResponder = null;
      } else if ($('.task .highlight').length > 0) {
        $('#project').click();
      }
    }

    if (e.which === 9) {
      // Tab
      if ($('#sort-dialog').is(':visible')) return;
      if ($('.editable .field').length > 0) saveField(e);
      e.preventDefault();
      if (!nextResponder) nextResponder = $('ul.details li.first');

      if (nextResponder.hasClass('last')) {
        nextResponder.click();
        nextResponder = nextResponder.closest('ul').find('li.first');
      } else {
        nextResponder.click();
      }

      return false;
    }
  });

  $(document).bind('keypress', function(e) {
    if ($(e.target).is(':input')) return;

    if (e.which === 35) {
      // # for delete
      var selectedTask = $('.task .highlight').first();
      if (selectedTask.length > 0) {
        $('.delete-button').click();
        e.preventDefault();
        return;
      }
    }

    if (e.which === 74) {
      // J
      var navItems = $('.outline-view li a'),
          selected = $('.outline-view li.selected a').first(),
          i = navItems.index(selected);
      $(navItems[i == navItems.length - 1 ? 0 : i + 1]).click();
      e.preventDefault();
      return;
    } else if (e.which === 75) {
      // K
      var navItems = $('.outline-view li a'),
          selected = $('.outline-view li.selected a').first(),
          i = navItems.index(selected);
      $(navItems[i == 0 ? navItems.length - 1 : i - 1]).click();
      e.preventDefault();
      return;
    }

    if (e.metaKey || e.altKey || e.ctrlKey || e.shiftKey) return;

    if (e.which === 106) {
      // j
      TasksController.closeEditors();
      var selectedTask = $('.task .highlight').first(),
          nextElement = $('.task .highlight').closest('li').next().find('.button');
      if (selectedTask.length === 0) {
        $('li.body li.task .button').first().click();
      } else if (nextElement.length > 0) {
        nextElement.click();
      } else {
        $('li.body li.task .button').first().click();
      }
    } else if (e.which === 107) {
      // k
      TasksController.closeEditors();
      var selectedTask = $('.task .highlight').first(),
          prevElement = $('.task .highlight').closest('li').prev().find('.button');
      if (selectedTask.length === 0) {
        $('li.body li.task .button').last().click();
        e.preventDefault();
      } else if (prevElement.length > 0) {
        prevElement.click();
        e.preventDefault();
      } else {
        $('li.body li.task .button').last().click();
        e.preventDefault();
      }
    } else if ((e.which === 32 || e.which === 79 || e.which === 111)
               && $('.task .highlight').length > 0) {
      // space or 'o'
      TasksController.open($('.task .highlight').closest('.button'));
      e.preventDefault();
    } else if (e.which === 110) {
      $('.task.add-button').click();
      e.preventDefault();
    } else if (e.which === 121) {
      $('#archive-tasks').click();
    } else if (e.which === 102) {
      // 'f' - folder menu
      $('.sort-task').click();
    } else if (e.which === 13) {
      var selectedTask = $('.task .highlight');
      if (selectedTask.length > 0) {
        selectedTask.prev('.state').click();
        e.preventDefault();
        return;
      } 
    }
  });
}

initKeyboardHandlers();


